$(document).ready(function(){

    var jsSlidein = $('.js-slidein1, .js-slidein2, .js-slidein3');

    $(window).width() < 1000 && jsSlidein.removeClass( '.js-slidein1, .js-slidein2, .js-slidein3' );

    jsSlidein.each(function(){

        var s = $(this).offset().top;

        $(window).scrollTop() + $(window).height() > s && $(this).removeClass( '.js-slidein1, .js-slidein2, .js-slidein3' )}),
        
        $(window).scroll(function(){
            jsSlidein.each(function(){

                var     s = $(this).offset().top + $(this).outerHeight() / 3,
                    move1 = $(window).scrollTop(),
                    move2 = $(window).scrollTop() - 130,
                    move3 = $(window).scrollTop() - 170;

move1 + $(window).height() > s && setTimeout( function(){ $('.js-slidein1').addClass("js-slidein-visible");}, 0);
move2 + $(window).height() > s && setTimeout( function(){ $('.js-slidein2').addClass("js-slidein-visible");}, 500);
move3 + $(window).height() > s && setTimeout( function(){ $('.js-slidein3').addClass("js-slidein-visible");}, 1000);

            })})});